window.addEventListener("DOMContentLoaded",(function(){window.screen.width<1200&&headerActive()}));const headerActive=()=>{const e=document.querySelector(".header"),t=document.querySelector("body");if(e){e.querySelector(".header__content_burger").onclick=()=>{e.classList.contains("active")?(e.classList.remove("active"),t.style.overflow=""):(e.classList.add("active"),t.style.overflow="hidden")}}};